    def testPart1(self):
        case1 = QRcreator.createQR_part1("Hello")
        self.assertEqual(case1, '010000000101010010000110010101101100011011000110111100001110110000010001')

        case2 = QRcreator.createQR_part1("")
        self.assertEqual(case2, '010000000000000011101100000100011110110000010001111011000001000111101100')

        case3 = QRcreator.createQR_part1("amogSUS")
        self.assertEqual(case3, '010000000111011000010110110101101111011001110101001101010101010100110000')

    def testErrorCorrection(self):
        case1 = QRcreator.createErrorCorrectionH('010000000101010010000110010101101100011011000110111100001110110000010001')
        self.assertEqual(case1, [179, 255, 87, 94, 24, 206, 76, 200, 89, 210, 111, 240, 217, 105, 88, 58, 24])
        
        case2 = QRcreator.createErrorCorrectionH('010000000000000011101100000100011110110000010001111011000001000111101100')
        self.assertEqual(case2, [109, 153, 248, 229, 74, 104, 103, 142, 121, 138, 114, 170, 163, 130, 62, 221, 154])

        case3 = QRcreator.createErrorCorrectionH('010000000111011000010110110101101111011001110101001101010101010100110000')
        self.assertEqual(case3, [9, 251, 197, 36, 214, 99, 67, 45, 73, 96, 52, 173, 154, 36, 132, 110, 9])

